package ControlStatement;

public class p3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=221;
		int b=220;
		int c=222;
		 
		if (a>b )
		{
			if(a>c)
			{
				System.out.println(a);
			}
			else
			{
				System.out.println(c);
			}
		}
		else
		{
			if(b>c)
			{
				System.out.println(b);
			}
			else
			{
				System.out.println(c);
			}
		}

	}

}
