package Practice;

public class p2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=24;
		
		int i=2;
		if (num%i==0 && num%num==0 && num%1==0)
		{
			System.out.println(num + " is a prime number");
		}
		else
		{
			System.out.println(num + " is not prime number");
		}
			

	}

}
